mapper.select:
select h.hotPlace_id as hotPlaceId, m.name as writerName, hot_place_name as HotPlaceName, address, contents, image_address as ImgAddress, lat, lng, views, likes, created_date as createdDate, visited_date as visitedDate
from hotplaceinfo h join memberinfo m on (h.member_id = m.member_id)
where h.hotPlace_id = #{hotPlaceId}

select hotPlace_id AS hotPlaceId, m.name as writerName, hot_place_name AS HotPlaceName, views, visited_date AS visitedDate
from hotplaceinfo h join memberinfo m on (h.member_id = m.member_id)
order by likes desc, views desc limit 0, 5;

select hotPlace_id AS hotPlaceId, hot_place_name AS HotPlaceName, views, created_date AS createdDate
from hotplaceinfo h join memberinfo m on (h.member_id = m.member_id)
order by hotPlace_id asc limit #{startPostNum}, 10;
